package com.cloud.leasing.module.mine.followimport android.os.Bundleimport com.cloud.leasing.Rimport com.cloud.leasing.base.BaseActivityimport com.cloud.leasing.constant.PageNameimport com.cloud.leasing.databinding.ActivityMineFollowBindingimport com.gyf.immersionbar.ktx.immersionBarclass MineFollowActivity : BaseActivity<ActivityMineFollowBinding>(ActivityMineFollowBinding::inflate) {    override fun getPageName() = PageName.FOLLOW    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        initSystemBar()    }    private fun initSystemBar() {        immersionBar {            statusBarColor(R.color.color_0e62B8)            fitsSystemWindows(true)            statusBarDarkFont(true)            navigationBarColor(R.color.white)            navigationBarDarkIcon(true)        }    }}